// -------------------- Render Section --------------------
function createParagraph(text) {
  const p = document.createElement('p');
  p.innerHTML = text;
  p.classList.add("paragraphStyle");
  return p;
}

function renderSection() {
  if (!id || !infoData[id]) {
    contentDiv.innerHTML = `<h1>Info Not Found</h1><p>The requested information section doesn't exist.</p>`;
    document.title = "Info Not Found - Mogorche Info";
    return;
  }

  const section = infoData[id];
  contentDiv.innerHTML = "";

  const titleEl = document.createElement('h1');
  titleEl.classList.add("headingStyle");
  titleEl.textContent = section.title[currentLang];
  contentDiv.appendChild(titleEl);

  let currentIndex = 0;
  const paragraphEl = createParagraph(section.paragraphs[currentLang][currentIndex]);
  contentDiv.appendChild(paragraphEl);

  const navContainer = document.createElement('div');
  navContainer.classList.add('navButtons');

  const prevBtn = document.createElement('button');
  prevBtn.textContent = '← Previous';
  prevBtn.disabled = true;

  const nextBtn = document.createElement('button');
  nextBtn.textContent = 'Next →';
  nextBtn.disabled = section.paragraphs[currentLang].length <= 1;

  navContainer.appendChild(prevBtn);
  navContainer.appendChild(nextBtn);
  contentDiv.appendChild(navContainer);

  function updateParagraph(index) {
    paragraphEl.innerHTML = section.paragraphs[currentLang][index];
    prevBtn.disabled = index === 0;
    nextBtn.disabled = index === section.paragraphs[currentLang].length - 1;
  }

  prevBtn.addEventListener('click', () => {
    if (currentIndex > 0) currentIndex--, updateParagraph(currentIndex);
  });
  nextBtn.addEventListener('click', () => {
    if (currentIndex < section.paragraphs[currentLang].length - 1) currentIndex++, updateParagraph(currentIndex);
  });

  window.updateParagraph = updateParagraph;
  window.currentIndex = currentIndex;

  document.title = `${section.title[currentLang]} - Mogorche Info`;
}

// -------------------- Apply Translations --------------------
function applyTranslations(lang) {
  currentLang = lang;
  localStorage.setItem("lang", lang);

  // Update all elements with data-translate
  document.querySelectorAll("[data-translate]").forEach(el => {
    const key = el.getAttribute("data-translate");
    const text = translations[lang][key];
    if (!text) return;

    if (el.tagName === "TITLE") document.title = text;
    else if (el.tagName === "A" && el.querySelector("i")) {
      const icon = el.querySelector("i");
      el.textContent = " " + text;
      el.prepend(icon);
    } else el.textContent = text;
  });

  // Update dynamic section
  if (id && infoData[id] && typeof window.updateParagraph === "function") {
    document.querySelector(".headingStyle").textContent = infoData[id].title[lang];
    window.updateParagraph(window.currentIndex);
  }

  // Update login popup if exists
  const popup = document.querySelector(".popupModal");
  if (popup) {
    popup.querySelector("h2").textContent = translations[lang].loginComingSoon;
    popup.querySelector("p").textContent = translations[lang].stayTuned;
  }
}

// -------------------- Language Selector --------------------
const customSelect = document.getElementById("customSelect");
const selected = customSelect.querySelector(".selected");
const options = customSelect.querySelector(".options");
const optionItems = customSelect.querySelectorAll(".options li");

selected.addEventListener("click", () => options.classList.toggle("show"));

optionItems.forEach(option => {
  option.addEventListener("click", () => {
    selected.innerHTML = option.innerHTML;
    options.classList.remove("show");
    applyTranslations(option.dataset.value);
  });
});

document.addEventListener("click", (e) => {
  if (!customSelect.contains(e.target)) options.classList.remove("show");
});

// -------------------- Login Popup --------------------
const loginBtn = document.querySelector(".fa-circle-user");
const popup = document.querySelector(".popupModal");
if (loginBtn && popup) {
  const closeBtn = popup.querySelector(".closeBtn");
  loginBtn.addEventListener("click", () => popup.style.display = "block");
  closeBtn.addEventListener("click", () => popup.style.display = "none");
  window.addEventListener("click", e => { if (e.target === popup) popup.style.display = "none"; });
}

// -------------------- Initialize --------------------
document.addEventListener("DOMContentLoaded", () => {
  // Set dropdown to saved language
  const activeOption = [...optionItems].find(opt => opt.dataset.value === currentLang);
  if (activeOption) selected.innerHTML = activeOption.innerHTML;

  renderSection();
  applyTranslations(currentLang);
});
